  tr.display_name as "Region" 
  , tc.country_name as "Country"
  , tc.country_iso_a2 as "ISO 2-letter code"
	, CONCAT(ta.asset_name, ' ', tad.asset_detail_name)as "Project"
  , tad.asset_detail_name as "Trains"
  , tt.companies as "Company"
  , NULLIF(TO_CHAR(tad.ea_start_date, 'DD/MM/YYYY'), '01/12/2078') as "Start date"
  , NULLIF(extract(year from tad.ea_start_date)::varchar(4), '2078') as "Start Year"
  , tat2.value as "MTPA"
  , ts.display_name as "Status"
  , tas.asset_subtype_name as "Type"
  , tadlt.fta_approval as "FTA"
  , tadlt.non_fta_approval as "Non-FTA"
  , tadlt.ferc_approval as "FERC Approval"
  , tadlt.export_licence as "Export Licence"
  , TO_CHAR(tadlt.final_investment_date, 'DD/MM/YYYY') as "FID"
  , tadn2.display_value as "Notes"
  , tad.likelihood as "Deterministic Likelihood"

FROM asset.tbl_asset_detail tad
	-- Asset --
  inner join asset.tbl_asset ta on tad.asset_id = ta.id

	-- Subtype --
  inner JOIN asset.tbl_asset_subtype tas ON tad.asset_subtype_id = tas.id

  -- Asset Type
  inner JOIN asset.tbl_asset_type tat ON tas.parent_asset_type_id = tat.id

  -- Timeseries
  inner join (select tat1.asset_detail_id, tat1.value from asset.tbl_asset_timeseries tat1
            	where tat1.timeseries_type_id = (select id from asset.tbl_timeseries_type ttt
                              where ttt.asset_type_id = 5
                              and ttt.is_default = True)) tat2
	on tat2.asset_detail_id = tad.id

  -- Liquefaction Specifics
  inner join asset.tbl_asset_detail_liquefaction_terminal tadlt on tad.id = tadlt.asset_detail_id 

  -- Notes (Only external)
  left join (select asset_detail_id, note_type_id, max(date_created) as max_date from asset.tbl_asset_detail_note notes
			group by asset_detail_id, note_type_id
			having note_type_id = 2) tadn		-- 2 is the id for external notes
	on tad.id = tadn.asset_detail_id
  left join asset.tbl_asset_detail_note tadn2 
  on tadn2.note_type_id = tadn.note_type_id and tadn2.asset_detail_id = tadn.asset_detail_id and tadn2.date_created = tadn.max_date

  -- Metadata

  -- Country
  inner join metadata.tbl_country tc on ta.country_id = tc.id

  -- Energy Product
