function ongoing_outages(row){
  // Address case where last_date_to/from is future/undefined

  if(radioOutageType.value == 'Planned') {return moment(row.last_planned_event_date_from)  <= moment() && moment() <= moment(row.last_planned_event_date_to)} 
  else if (radioOutageType.value == 'Unplanned') {return moment(row.last_unplanned_event_date_from)  <= moment() && moment() <= moment(row.last_unplanned_event_date_to)} 
  else if (radioOutageType.value == 'Either') {return (moment(row.last_planned_event_date_from)  <= moment() && moment() <= moment(row.last_planned_event_date_to)) || (moment(row.last_unplanned_event_date_from)  <= moment() && moment() <= moment(row.last_unplanned_event_date_to))} 
  else {return (moment(row.last_planned_event_date_from)  <= moment() && moment() <= moment(row.last_planned_event_date_to)) && (moment(row.last_unplanned_event_date_from)  <= moment() && moment() <= moment(row.last_unplanned_event_date_to))} 
}

