WITH insert_asset AS (
  INSERT INTO asset.tbl_asset (asset_type_id, asset_name, status_id, latitude, longitude, country_id, ea_start_date, ea_end_date, source_start_date, source_end_date, created_by, date_created, updated_by, date_updated)
  SELECT 
      {{filterAssetType.value}}
    , NULLIF({{assetAddName.value}}, '')
    , {{assetAddStatusID.value}}
    , NULLIF({{assetAddLatitude.value}}, '')::real
    , NULLIF({{assetAddLongitude.value}}, '')::real
    , {{assetAddCountryID.value}}
    , NULLIF({{assetAddStartDateEA.formattedString}}, '')::TIMESTAMP
    , COALESCE( NULLIF({{assetAddEndDateEA.formattedString}}, ''), '2079-01-01')::TIMESTAMP
    , NULLIF({{assetAddStartDateSource.formattedString}}, '')::TIMESTAMP
    , NULLIF({{assetAddEndDateSource.formattedString}}, '')::TIMESTAMP
    , {{get_nickname.data}}
    , {{moment()}}
    , {{get_nickname.data}}
    , {{moment()}}

  RETURNING id as new_asset_id
)

-- Asset Specifics
, insert_refinery_specifics AS (
	INSERT INTO asset.tbl_asset_refinery(asset_id, is_teapot, is_state_owned, created_by, date_created, updated_by, date_updated)
  SELECT 
      insert_asset.new_asset_id
  	, {{assetAddSpecificsRefineryIsTeapot.value}}
  	, {{assetAddSpecificsRefineryIsStateOwned.value}}
  	, {{get_nickname.data}}
    , {{moment()}}
    , {{get_nickname.data}}
    , {{moment()}}
  FROM insert_asset
  /* Only applies for Refinery */
  WHERE {{filterAssetType.displayValue}} = 'Refinery'
)

-- Ownership
, insert_ownership AS (
  INSERT INTO asset.tbl_asset_ownership(asset_id, company_id, ownership_pct, created_by, date_created, updated_by, date_updated)
  SELECT
  	insert_asset.new_asset_id
  , x."Company Name"
  , x."Ownership"
  , {{get_nickname.data}}
  , {{moment()}}
  , {{get_nickname.data}}
  , {{moment()}}

  FROM insert_asset
   , json_to_recordset({{transformAssetAddOwnershipTable.value}}) -- might need to use Transformer here 
  	as x(id int
         , "Company Name" INT
         , "Ownership" FLOAT)
)

-- Always TRUE, executes the chain of all above CTE
