.embedded-canvas {background-color: #ffffff !important;
}

:root {box-shadow: none !important;}

  .right-label {display:none !important;}

  .table-cell-action > button {
    //background-color: #F5F5F5;
    border: solid 1px;
    border-color: lightgray
  }
  .retool-modal-component-wrapper {
    background-color: RGBA(0,0,0,0.32)    
  }

  ._retool-assetAddOwnershipTable  > div .-center {
    z-index: -1;
  }

  ._retool-assetEditOwnershipTable > div .-center {
    z-index: -1;
  }

  .custom-theme-overrides .retool-canvas ._retool-FormWidget .retool-form-floating-action-bar .ant-btn[disabled], ._retool-exploreClearFiltersButton, ._retool-downloadButton {
