    background-color: #f5f5f5!important;
    border-color: #d9d9d9!important;
    cursor: not-allowed!important;}

  .widget-alert.info{
    border: 1px solid #b1d3f1FF;
    background-color: #b1d3f187
  }
  .widget-alert.warning{
    border: 1px solid #fbd9a8ff;
    background-color: #fffbf5ff
  }
  [class*="_retool-formValidation"] {
    background-color: #EBBCB787;
    padding: 15px 7px;
    border-radius: 4px;
    border: 1px solid darkred
  }

  /* Table > native buttons*/
  .custom-theme-overrides .retool-canvas ._retool-TableWidget .ReactTable .pagination-bottom .-pagination .table-actions .ant-btn {
    color: white;
    background-color: #990033;
    margin: 1px;
    height: 2.25em;
    padding: 1px 5px;
    transition: all 50s;
  } 

  /*Summary Statistic Visual Aid*/  
  ._retool-summaryCurrentOutages .ant-statistic-content { {{styleSummaryCurrentOutages.value}}   
  }

  ._retool-summaryOperableCapacity .ant-statistic-content { {{styleSummaryOperableCapacity.value}}
  }
